import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
log.info "Hello this is my first program"

//context prop of test case
log.info context.expand('${#TestCase#name}')
log.info context.expand('${#TestCase#age}')
//to access any property in TR you have to come thru its parent only
log.info testRunner.testCase.testSuite.testCases["getEmployee"].getPropertyValue("id")
log.info testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("age")

//log.info testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("age") = '15'log.info testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("age")

//GETTING WHOLE REQUEST-----XML--------////////XML==------<<<<<
def addReq = testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"].getPropertyValue("Request")
def name = testRunner.testCase.testSuite.testCases["getEmployee"].getPropertyValue("name")

//Save XML
def xmlAdd= new XmlHolder(addReq)
//XML set node value vairable
xmlAdd.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)
//new XML with node value variable name
def newAddXml=xmlAdd.getXml();

//Set testStep get = new Request
testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"].setPropertyValue("Request",newAddXml)
//testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"].getPropertyValue("Request",newAddXml)

//TRIGGER SOAP REQUEST
//Create oject for context of teststep to run for get TestStep
def addTestStep = testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"]
//Create object for this class
//pass context and create 
def contextGetEmployee = new WsdlTestRunContext(addTestStep);

addTestStep.run(testRunner,contextGetEmployee)
//contexgt of getEmployee should be passed as AddTest step which we are trying to run belongs to that test case

